CREATE TABLE BOARD.ATTACHMENT
(
    NO VARCHAR2(50) PRIMARY KEY NOT NULL,
    POSTNO VARCHAR2(50),
    ATT BLOB,
    CONSTRAINT FK_POST_TO_ATTACHMENT FOREIGN KEY (POSTNO) REFERENCES BOARD.POST (NO) ON DELETE CASCADE
);
COMMENT ON COLUMN BOARD.ATTACHMENT.NO IS '첨부파일번호';
COMMENT ON COLUMN BOARD.ATTACHMENT.POSTNO IS '게시글번호';
COMMENT ON COLUMN BOARD.ATTACHMENT.ATT IS '첨부파일';
CREATE TABLE BOARD.BOARD
(
    NO VARCHAR2(50) PRIMARY KEY NOT NULL,
    NAME VARCHAR2(30) NOT NULL,
    USE VARCHAR2(1) NOT NULL,
    CREATOR VARCHAR2(14),
    RDATE DATE NOT NULL,
    CONSTRAINT FK_MEMBER_TO_BOARD FOREIGN KEY (CREATOR) REFERENCES BOARD.MEMBER (ID)
);
COMMENT ON COLUMN BOARD.BOARD.NO IS '게시판번호';
COMMENT ON COLUMN BOARD.BOARD.NAME IS '게시판이름';
COMMENT ON COLUMN BOARD.BOARD.USE IS '게시판사용여부';
COMMENT ON COLUMN BOARD.BOARD.CREATOR IS '게시판등록자';
COMMENT ON COLUMN BOARD.BOARD.RDATE IS '게시판등록일';
CREATE TABLE BOARD.COMMENTS
(
    NO VARCHAR2(50) PRIMARY KEY NOT NULL,
    POSTNO VARCHAR2(50),
    CONTENT VARCHAR2(500) NOT NULL,
    WRITER VARCHAR2(14),
    RDATE DATE,
    CONSTRAINT FK_POST_TO_COMMENTS FOREIGN KEY (POSTNO) REFERENCES BOARD.POST (NO) ON DELETE CASCADE,
    CONSTRAINT FK_MEMBER_TO_COMMENTS FOREIGN KEY (WRITER) REFERENCES BOARD.MEMBER (ID)
);
COMMENT ON COLUMN BOARD.COMMENTS.NO IS '댓글번호';
COMMENT ON COLUMN BOARD.COMMENTS.POSTNO IS '게시글번호';
COMMENT ON COLUMN BOARD.COMMENTS.CONTENT IS '댓글내용';
COMMENT ON COLUMN BOARD.COMMENTS.WRITER IS '댓글작성자';
COMMENT ON COLUMN BOARD.COMMENTS.RDATE IS '댓글작성일시';
CREATE TABLE BOARD.MEMBER
(
    ID VARCHAR2(14) PRIMARY KEY NOT NULL,
    NAME VARCHAR2(50) NOT NULL,
    PASS VARCHAR2(255) NOT NULL,
    TEL VARCHAR2(30) NOT NULL,
    BIRTH DATE NOT NULL,
    EMAIL VARCHAR2(40) NOT NULL,
    ZIPCODE VARCHAR2(5) NOT NULL,
    ADDR1 VARCHAR2(255) NOT NULL,
    ADDR2 VARCHAR2(255),
    PROFILE VARCHAR2(255)
);
COMMENT ON COLUMN BOARD.MEMBER.ID IS '아이디';
COMMENT ON COLUMN BOARD.MEMBER.NAME IS '이름';
COMMENT ON COLUMN BOARD.MEMBER.PASS IS '비밀번호';
COMMENT ON COLUMN BOARD.MEMBER.TEL IS '핸드폰번호';
COMMENT ON COLUMN BOARD.MEMBER.BIRTH IS '생년월일';
COMMENT ON COLUMN BOARD.MEMBER.EMAIL IS '이메일';
COMMENT ON COLUMN BOARD.MEMBER.ZIPCODE IS '우편번호';
COMMENT ON COLUMN BOARD.MEMBER.ADDR1 IS '도로명주소';
COMMENT ON COLUMN BOARD.MEMBER.ADDR2 IS '상세주소';
COMMENT ON COLUMN BOARD.MEMBER.PROFILE IS '프로필사진';
CREATE TABLE BOARD.POST
(
    NO VARCHAR2(50) PRIMARY KEY NOT NULL,
    TITLE VARCHAR2(90) NOT NULL,
    CONTENT CLOB,
    RDATE DATE,
    WRITER VARCHAR2(14),
    BOARDNO VARCHAR2(50),
    RECURSION VARCHAR2(50),
    GROUPNO VARCHAR2(50),
    CONSTRAINT FK_MEMBER_TO_POST FOREIGN KEY (WRITER) REFERENCES BOARD.MEMBER (ID),
    CONSTRAINT FK_BOARD_TO_POST FOREIGN KEY (BOARDNO) REFERENCES BOARD.BOARD (NO) ON DELETE CASCADE,
    CONSTRAINT FK_POST_TO_POST FOREIGN KEY (RECURSION) REFERENCES BOARD.POST (NO)
);
COMMENT ON COLUMN BOARD.POST.NO IS '게시글번호';
COMMENT ON COLUMN BOARD.POST.TITLE IS '제목';
COMMENT ON COLUMN BOARD.POST.CONTENT IS '내용';
COMMENT ON COLUMN BOARD.POST.RDATE IS '작성일시';
COMMENT ON COLUMN BOARD.POST.WRITER IS '작성자 ID';
COMMENT ON COLUMN BOARD.POST.BOARDNO IS '게시판번호';
COMMENT ON COLUMN BOARD.POST.RECURSION IS '부모게시글번호';
